monitoring_results.query:
PREFIX frbr: <http://purl.org/vocab/frbr/core#>
PREFIX fabio: <http://purl.org/spar/fabio/>

ASK {
  {
    SELECT (COUNT(DISTINCT ?br) AS ?count) {
      ?br frbr:partOf ?venue1 ;
        frbr:partOf ?venue2 . 
      # ?venue1 and ?venue2 have the same type (any type)
      ?venue1 a ?type .
      ?venue2 a ?type .
      FILTER(?venue1 != ?venue2)
      FILTER(?type != fabio:Expression)  # Exclude fabio:Expression type
    }
  }
  FILTER (?count > 1000)
}

